(132988, 2, 0, 'Okay... stop!', 12, 0, 100, 0, 0, 105737, 146094, 'Patch to Player'),
(132988, 3, 0, 'Whoa! You got their attention now! Big horde incoming!', 14, 0, 100, 0, 0, 105706, 148006, 'Patch to Player'),
(133077, 0, 0, 'Fresh meats!', 12, 0, 100, 0, 0, 98362, 143873, 'Nazmani War Slave to Player'),
(133125, 0, 0, 'Good, de path is open! Now all dat remains is to clean out de vermin.', 12, 0, 100, 0, 0, 101997, 146413, 'Princess Talanji to Player'),
(133472, 0, 0, 'Da end of da Zandalari begins now!', 14, 0, 100, 0, 0, 116582, 147046, 'Grand Ma\'da Ateena'),
(133472, 1, 0, 'Scream for mercy!', 14, 0, 100, 0, 0, 116582, 150765, 'Grand Ma\'da Ateena'),
