(122103, 0, 0, 'Auggh!', 14, 0, 100, 0, 0, 0, 131078, 'Blood Scavenger to Player'),
(122204, 0, 0, 'Your blood gonna fuel us!', 12, 0, 100, 0, 0, 98335, 144298, 'Blood Witch Najima to Player'),
(122204, 1, 0, 'Dis changes... nothing! Soon... you all gonna... die...', 12, 0, 100, 0, 0, 98346, 133198, 'Blood Witch Najima to Player'),
(122793, 0, 0, 'Bwonsamdi bless you!', 12, 0, 100, 603, 0, 0, 148349, 'Ouda to Player'),
